layout(set = 1, binding = 2) uniform sampler2D fluxayTex;
layout(set = 1, binding = 1) uniform param {
  vec4 uvOffset;
  vec4 color;
  float uvRotated;
  float speed;
  float interval;
};
vec4 frag () {
  vec4 o = color;
  o *= CCSampleWithAlphaSeparated(cc_spriteTexture, v_uv0);
  #if IS_GRAY
    float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
    o.r = o.g = o.b = gray;
  #endif
  ALPHA_TEST(o);
  vec2 uvNormalize;
  uvNormalize.x = (v_uv0.x - uvOffset.x) / (uvOffset.z - uvOffset.x);
  uvNormalize.y = (v_uv0.y - uvOffset.y) / (uvOffset.w - uvOffset.y);
  if (uvRotated > 0.5) {
    float temp = uvNormalize.x;
    uvNormalize.x = uvNormalize.y;
    uvNormalize.y = 1.0 - temp;
  }
  float timeStep = mod(cc_time.x * speed, interval);
  uvNormalize.x -= timeStep - 1.0;
  o.rgb += texture(fluxayTex, uvNormalize).rgb;
  o *= v_color;
  return o;
